import e from"../../../src/controller/Component.mjs";export default Neo.setupClass(class extends e{static config={className:"Form.view.FormContainerController"};onComponentConstructed(){super.onComponentConstructed(),this.component.on({fieldChange:e=>console.log("fieldChange",e),fieldFocusLeave:e=>console.log("fieldFocusLeave",e),fieldUserChange:e=>console.log("fieldUserChange",e)})}onNextPageButtonClick(e){this.getStateProvider().data.activeIndex++}onPrevPageButtonClick(e){this.getStateProvider().data.activeIndex--}async onSaveButtonClick(e){let o=this.getReference("main-form"),t=await o.getSubmitValues();Neo.main.addon.LocalStorage.updateLocalStorageItem({appName:this.component.appName,key:"neo-form",value:JSON.stringify(t)})}});