import e from"./Base.mjs";export default Neo.setupClass(class extends e{static config={className:"Neo.main.addon.Cookie",remote:{app:["getCookie","getCookies","setCookie"]}};getCookie(e){let{cookie:o}=document.split("; ").find((o=>o.startsWith(e)));return o?o.split("=")[1]:null}getCookies(){return document.cookie}setCookie(e){document.cookie=e}});